  function GetPointLength(iPoint)
  {
    if(iPoint<=0){
      return 0.0;
    }
    else if(iPoint>1000000){
      return 100.0;
    }
    arrayPoint = new Array(0,100,300,700,1000,1500,2000,2500,3000,3600,4200,4800,
                           5400,6000,6800,7600,8400,9200,10000,11000,12000,13000,
                           14000,15000,17000,19000,21000,23000,25000,28000,31000,
                           34000,37000,40000,45000,50000,55000,60000,65000,75000,
                           90000,110000,140000,180000,230000,300000,400000,550000,
                           750000,1000000);
    for(i=0;i<arrayPoint.length-1;i++){
      if(iPoint>arrayPoint[i] && iPoint<=arrayPoint[i+1]){
        fTemp = (iPoint-arrayPoint[i])/(arrayPoint[i+1]-arrayPoint[i]);
        return fTemp*100.0;
      }
    }
  }
